mapper.select:
SELECT COUNT(*) FROM FOODORDER WHERE STORENAME=#{storename}
AND ${Q} LIKE '%'||#{D}||'%'
AND STATE='승인대기' OR STATE='승인완료' ORDER BY ORDERTIME DESC

SELECT COUNT(*) FROM FOODORDER WHERE STORENAME=#{storename}
AND ${Q2} LIKE '%'||#{D2}||'%'
AND STATE='배달완료' ORDER BY ORDERTIME DESC

		SELECT STORENAME, BRANDNO, LOCATION, GPA, HOURS, TEL, MINPRICE, INFO
		FROM STORE
		WHERE STORENAME=#{storename}
